<script>
  document.addEventListener('DOMContentLoaded', function() {
    // Only show the first 6 items initially
    const items = document.querySelectorAll('.project-item');
    items.forEach((item, index) => {
      item.style.display = index < 6 ? 'block' : 'none';
    });
  });

  function filterSelection(category) {
    const items = document.querySelectorAll('.project-item');
    
    if (category === 'all') {
      // Show only the first 6 items again when 'all' is selected
      items.forEach((item, index) => {
        item.style.display = index < 6 ? 'block' : 'none';
      });
    } else {
      // Hide all items first
      items.forEach(item => item.style.display = 'none');

      // Show items that match the selected category
      let count = 0;
      items.forEach(item => {
        if (item.classList.contains(category) && count < 6) {
          item.style.display = 'block';
          count++;
        }
      });
    }
  }
</script>

